$(document).ready(function(){$("#CreateAdDetailInputModel_CategoryId").on("change",function(){var n=$(this).find("option:selected").val();console.log(n);$.ajax({url:"GetSubcategories",type:"GET",data:"categoryId="+n,dataType:"json",success:function(n){$("#CreateAdDetailInputModel_SubCategoryId").empty();$("#CreateAdDetailInputModel_SubCategoryId").append("<option>--Select--<\/option>");$.each(n,function(n,t){$("#CreateAdDetailInputModel_SubCategoryId").append("<option value="+t.id+">"+t.name+"<\/option>");console.log(t.id)})}})})});