$(document).ready(function(){var n;$(".trElm").click(function(){n=$(this)});$(".archive").click(function(){var t=$(this).val();console.log(t);$.ajax({url:"ArchiveAd",type:"GET",data:"adId="+t,dataType:"json",success:function(){n.hide("slow",function(){n.remove()});$(function(){$("#modal").modal("show");$(".modal-message").html("You have successfully archived this ad!");$(function(){var n=$("#modal");clearTimeout(n.data("hideInterval"));n.data("hideInterval",setTimeout(function(){n.modal("hide")},2500))})})}})})});$(document).ready(function(){var n;$(".removeElement").click(function(){n=$(this)});$(".activate").click(function(){var t=$(this).val();console.log(t);$.ajax({url:"ActivateAd",type:"GET",data:"adId="+t,dataType:"json",success:function(){n.hide("slow",function(){n.remove()});$(function(){$("#modal").modal("show");$(".modal-message").html("You have successfully activated this ad!");$(function(){var n=$("#modal");clearTimeout(n.data("hideInterval"));n.data("hideInterval",setTimeout(function(){n.modal("hide")},2500))})})}})})});$(".custom-file-input").on("change",function(){var n=$(this).val().split("\\").pop();$(this).siblings(".custom-file-label").addClass("selected").html(n)});